import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './ImageUploader.css';

const ImageUpload = () => {
  const [selectedFile, setSelectedFile] = useState(null);
  const [uploadStatus, setUploadStatus] = useState('');
  const navigate = useNavigate(); // Hook to navigate to different routes

  const onFileChange = event => {
    // Reset upload status
    setUploadStatus('');
    // Update the state with selected file
    setSelectedFile(event.target.files[0]);
  };

  const onFileUpload = () => {
    if (selectedFile) {
      const reader = new FileReader();

      reader.onloadend = function() {
        const base64String = reader.result
          .replace('data:', '')
          .replace(/^.+,/, '');

        axios.post("https://8cixrhnhu1.execute-api.us-east-1.amazonaws.com/dev/upload-image", {
          body: base64String
        })
        .then(response => {
          console.log("File uploaded successfully", response);
          setUploadStatus('Upload successful!');

          // Wait for some time before navigating, e.g., 3 seconds
          setTimeout(() => {
            navigate('/sentiment');
          }, 3500);  
        })
        .catch(error => {
          console.error("Error uploading file", error);
          setUploadStatus('Error uploading file.');
        });
      };

    reader.readAsDataURL(selectedFile);
    } 
    else {
      setUploadStatus('Please select a file to upload.');
    }
  };

  // File content to be displayed after file upload is complete
  const fileData = () => {  
    if (selectedFile) {
      return (
        <div>
          <h2>File Details:</h2>
          <p>File Name: {selectedFile.name}</p>
          <p>File Type: {selectedFile.type}</p>
          <p>Last Modified: {selectedFile.lastModifiedDate.toDateString()}</p>
        </div>
      );
    } else {
      return (
        <div>
          <br />
          <h4>Select a file before pressing the upload button</h4>
        </div>
      );
    }
  };

  return (
    <div>
      <h1>Upload an Image</h1>
      <input type="file" onChange={onFileChange} />
      <button onClick={onFileUpload}>
        Upload!
      </button>
      {fileData() } { <p>{uploadStatus}</p>}
    </div>
  );
}

export default ImageUpload;
