import React, { useState } from 'react';
import axios from 'axios';

const ImageUpload = () => {
  const [selectedFile, setSelectedFile] = useState(null);

  const onFileChange = event => {
    // Update the state
    setSelectedFile(event.target.files[0]);
  };

  const onFileUpload = () => {
    // Check if a file is selected
    if (selectedFile) {
      const reader = new FileReader();

      reader.onloadend = function() {
        const base64String = reader.result
          .replace('data:', '')
          .replace(/^.+,/, '');

        axios.post("https://8cixrhnhu1.execute-api.us-east-1.amazonaws.com/dev/upload-image", {
          image: base64String // Assuming the API expects a JSON object with a base64 string
        })
        .then(response => {
          console.log("File uploaded successfully", response);
          // You can add code here to handle the successful upload, such as setting state or displaying a message.
        })
        .catch(error => {
          console.error("Error uploading file", error);
          // You can add code here to handle the upload error, such as setting state or displaying a message.
        });
      };

      reader.readAsDataURL(selectedFile);
    } else {
      console.log('Please select a file to upload');
      // You can add code here to handle the case where no file is selected before upload.
    }
  };

  // File content to be displayed after file upload is complete
  const fileData = () => {
    if (selectedFile) {
      return (
        <div>
          <h2>File Details:</h2>
          <p>File Name: {selectedFile.name}</p>
          <p>File Type: {selectedFile.type}</p>
          <p>Last Modified: {selectedFile.lastModifiedDate.toDateString()}</p>
        </div>
      );
    } else {
      return (
        <div>
          <br />
          <h4>Select a file before pressing the upload button</h4>
        </div>
      );
    }
  };

  return (
    <div>
      <h1>Upload an Image</h1>
      <input type="file" onChange={onFileChange} />
      <button onClick={onFileUpload}>
        Upload!
      </button>
      {fileData()}
    </div>
  );
}

export default ImageUpload;
